const focusOnFirstInputElement = () => {
    document.getElementById("username").focus();
};

function makePasswordToggle(id) {
    var container = document.getElementById(id);
    var password = container.getElementsByTagName("input")[0];
    var toggler = document.getElementById("togglePassword");

    function showHidePassword() {
        if (password.type == "password") {
            password.setAttribute("type", "text");
            toggler.classList.add("text-input__icon--view-hidden");
            toggler.classList.remove("text-input__icon--hidden");
        } else {
            toggler.classList.add("text-input__icon--hidden");
            toggler.classList.remove("text-input__icon--view-hidden");
            password.setAttribute("type", "password");
        }
        password.focus();
    };

    toggler.addEventListener("click", showHidePassword);
}

function makeFloatLabelToggle(containerId) {
    var container = document.getElementById(containerId);
    var input = container.getElementsByTagName("input")[0];

    function toggleFloatLabel(e) {
        if (e.target.value !== '') {
            container.classList.remove("placeholder-shown");
        } else {
            container.classList.add("placeholder-shown");
        }
        input.focus();
    }

    input.addEventListener('input', toggleFloatLabel);
}

function start() {
    makePasswordToggle("passwordContainer");
    makeFloatLabelToggle("usernameContainer");
    makeFloatLabelToggle("passwordContainer");
    focusOnFirstInputElement();
}

if (document.readyState === "loading") {
    // Loading hasn't finished yet
    document.addEventListener("DOMContentLoaded", start);
} else {
    // `DOMContentLoaded` has already fired
    start();
}

