    function toggle() {
        if (input.type == "password") {
            input.setAttribute("type", "text");
            toggler.classList.add("text-input__icon--view-hidden");
            toggler.classList.remove("text-input__icon--hidden");
        } else {
            toggler.classList.add("text-input__icon--hidden");
            toggler.classList.remove("text-input__icon--view-hidden");
            input.setAttribute("type", "password");
        }
        input.focus();
    };

    if (toggler) {
        toggler.addEventListener("click", toggle);
    }
}

function makeFloatLabelToggle(container) {
    var input = container.getElementsByTagName("input")[0];

    function toggleFloatLabel(e) {
        if (e.target.value !== "") {
            container.classList.remove("placeholder-shown");
        } else {
            container.classList.add("placeholder-shown");
        }
    }

    input.addEventListener("input", toggleFloatLabel);
}

function makeSubmitButtonToggle() {
    let submitButton = document.getElementById('submitButton');

    // save button should be initially disabled
    submitButton.setAttribute("disabled", "");

    let requiredInputs = [
        document.getElementById('currentPassword'),
        document.getElementById('newPassword')
    ];

    for (i = 0; i < requiredInputs.length; i++) {
        requiredInputs[i].addEventListener("input", () => {
            let inputValues = [];
            requiredInputs.forEach((input) => inputValues.push(input.value));

            if (inputValues.indexOf("") !== -1) {
                submitButton.disabled = true;
                submitButton.classList.add("button--disabled");
            } else {
                submitButton.disabled = false;
                submitButton.classList.remove("button--disabled");
            }
        });
    }
}

function start() {
    Array.from(document.getElementsByClassName("float-label")).forEach(makeFloatLabelToggle);
    Array.from(document.getElementsByClassName("visibility-toggle")).forEach(makeInputVisibilityToggle);
    makeSubmitButtonToggle();
    focusOnFirstInputElement();
}

if (document.readyState === "loading") {
    // Loading hasn't finished yet
    document.addEventListener("DOMContentLoaded", start);
} else {
    // `DOMContentLoaded` has already fired
    start();
}

