sktemplate.script:
const deviceId = document.getElementById("deviceId");
const li = document.getElementsByClassName("device");

if(window.addEventListener) {
   // Normal browsers
   for (i = 0; i < li.length; i++) {
      li[i].addEventListener('click', deviceDelected, false);
   } 

} else
   if(window.attachEvent) {
      // IE
      for (i = 0; i < li.length; i++) {
        li[0].attachEvent('onclick', deviceDelected);
     }   
   }

function deviceDelected(event) {
   for (i = 0; i < li.length; i++) {
      li[i].classList.remove("selected");
   } 
   event.target.parentElement.classList.add("selected");
   deviceId.value = event.target.parentElement.getAttribute('data-id');
}

