customScript.value:
const deviceId = document.getElementById("deviceId");
const li = document.getElementsByClassName("device");


if(window.addEventListener) {
   // Normal browsers
   for (i = 0; i < li.length; i++) {
      li[i].addEventListener('click', deviceDelected, false);
   } 

} else
   if(window.attachEvent) {
      // IE
      for (i = 0; i < li.length; i++) {
        li[i].attachEvent('onclick', deviceDelected);
     }   
   }

function deviceDelected(event) {
   for (i = 0; i < li.length; i++) {
      li[i].classList.remove("selected");
   } 
   const selected = event.target;
   const device = selected.closest('li.device');
   console.log("device",device);
   device.classList.add("selected");
   deviceId.value = device.getAttribute('data-id');
}

function initSelection() {
   document.getElementById("deviceId").value = li[0].getAttribute('data-id');
}
li[0].classList.add("selected");
setTimeout(initSelection, 100);

