	if (str && str.length>len) {
		var xStr = str.slice(str.length - len);
		return '******'+ xStr;
	}
	return str
}

function emailDisplay(str, len) {
	if (str && str.length>len && str.indexOf("@")>0) {
		var begin = str.substring(0, len);
		var position = str.indexOf("@");
		var end = str.slice(position);
		return begin+ '****'+ end;
	}
	return str
}

module.exports = a = async ({params}) => {
	console.log('params: ', params)
	return {'SMSBackupDisplay': setDisplay(params.SMSBackup, 2), 'phoneBackupDisplay': setDisplay(params.phoneBackup, 2), 'emailBackupDisplay': emailDisplay(params.emailBackup, 2)}
}

