    }).catch(function (err) {
        document.forms['webAuthenSupportForm'].elements["webAuthenSupport"].value = authSuported ? "SECURITY_KEY_ONLY" : "NONE";
        document.getElementById('autoSubmit').click();
    });
}, 200);

