customScript.value:
const deviceId = document.getElementById("deviceId");
const li = document.getElementsByClassName("mandatory");


if(window.addEventListener) {
   // Normal browsers
   for (i = 0; i < li.length; i++) {
      li[i].addEventListener('click', deviceSelected, false);
   } 

} else
   if(window.attachEvent) {
      // IE
      for (i = 0; i < li.length; i++) {
        li[i].attachEvent('onclick', deviceSelected);
     }   
   }

function deviceSelected(event) {
   for (i = 0; i < li.length; i++) {
      li[i].classList.remove("selected");
   } 
   const selected = event.target;
   const device = selected.closest('li.mandatory');
   device.classList.add("selected");
   deviceId.value = device.getAttribute('data-id');
   inputChanged();
}


function inputChanged() {
   let submitButton  = document.getElementById('submit');
   if (deviceId.value.length===0) {
      submitButton.setAttribute("disabled", "true");
   }else{
          submitButton.removeAttribute("disabled");
       }
}

inputChanged();

