module.exports = a = async ({params}) => {
	var devices = JSON.parse(params.devices);
	let allExists = true;
	var methods = [];
	try {
		if (params.mandatoryMethods.length > 0){
		var mandatoryMethods = params.mandatoryMethods.split(' ');
		for (let i = 0; i < mandatoryMethods.length; i++) {
			let found = devices.filter(device => device.type === mandatoryMethods[i]);
			methods.push({type: mandatoryMethods[i], paired: found.length > 0});
			if (found.length===0){
				allExists = false;
			};
			}
		}
	}
	catch(err) {
	}

	let paired = methods.filter(method => method.paired);
	let unpaired = methods.filter(method => !method.paired);
	let sortedMethods = [...paired, ...unpaired];
	var showMandatoryMethods = allExists === false && devices.length > 0;


	return {showMandatoryMethods, methods: sortedMethods};
}

